              {  name: "Adam",   img: 'https://picsum.photos/251',   age: 66,    job: "Backend"   },
              {  name: "Kaz" ,   img: 'https://picsum.photos/252',   age: 32,    job: "AI"        },
      ]                                                                                     
    }
  }                        

  showInfo(matrix){

          const m = matrix.map(
// 
          person => 
            <Box
                          name = {  person.name }
                          img =  {  person.img  }
                          age =  {  person.age  }
                          job =  {  person.job  }
            />
          )

    return m;
  }

  render(){


    // Extract Every Single One


    return(
      <div className='mio'>
        {this.showInfo(this.state.member)}
      </div>
    )
  }


}


export default App
