import React from 'react'
import { Component } from 'react';
import "./SASS/style.css";

class AddItems extends Component{

    constructor(){
        super();
        this.state ={
            name: "",
            job : "",
            age : "",
        }
        this.addItem = this.addItem.bind(this)
    }


    addItem(e){

        this.setState({
            [e.target.id] : e.target.value
        })
        console.log(this.state);
    }


    render(){
        return(
            <div className=''>
                <input onChange={this.addItem}  id='name'  type="text" placeholder='Enter A Name : ' />
                <input onChange={this.addItem}  id='job'   type="text" placeholder='Enter A Job  : ' />
                <input onChange={this.addItem}  id='age'   type="text" placeholder='Enter An Age : ' />
            </div>  
        )
    }
}

export default AddItems
