import React, { Component } from 'react';
import AddItems from "./component/AddItems";
import TodoItems from "./component/TodoItems";

class App extends Component{

    constructor(){
        super();
        this.state = {
            info : [
                { name : "John"    },
                { name : "Adam"    },
                { name : "Micheal" },
            ]
        }
        this.adding = this.adding.bind(this)
        this.removing = this.removing.bind(this)
    }

    mapping(arr){
        const value = arr.map(
            (res, id) => <TodoItems
            name   = {  res.name      }
            key    = {  id            }
            id     = {  id            }
            removin= {  this.removing }
            />
        )
        return value
    }

    adding(item){
        const {info} = this.state
        item.id = Math.random()
        info.push(item)
        this.setState({
            info
        })

    }
    removing(id){
        const {info} = this.state
        info.findIndex( item => item.id === id)
        this.setState({
            info
        })
    }


    render(){
        return(
            <div>
                <AddItems adding = {  this.adding  }/>
                <ul>{this.mapping(this.state.info)}</ul>
            </div>
        )
    }
}

export default App
