import React, { Component } from 'react';
import AddItems from "./component/AddItems";
import TodoItems from "./component/TodoItems";

class App extends Component{

    constructor(){
        super();
        this.state = {
            info : [
                { name : "John"    , age : 23},
                { name : "Adam"    , age : 43},
                { name : "Micheal" , age : 15},
            ]
        }
        this.adding = this.adding.bind(this)
    }

    mapping(arr){
        const value = arr.map(
            (res, id) => <TodoItems
            name   = {  res.name      }
            age    = {  res.age       }
            key    = {  id            }
            adding = {  res.adding(id)  }

            />
        )
        return value
    }

    adding(newInfo){
        const {info} = this.state

        info.push(newInfo)

        this.setState({
            info
        })

    }


    render(){
        return(
            <div>
                <AddItems/>
                <ul>{this.mapping(this.state.info)}</ul>
            </div>
        )
    }
}

export default App
