import React from 'react'
import "./SASS/style.css";
import { faCheck, faThumbsUp, faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


const TodoItems = (props)=>{

    const active = ()=>{
        document.querySelectorAll('.icon').forEach( e => { 
            console.log(e.className);
            e.classList.toggle= "active";  });
    }
    return(

        <div key={props.id} className='box'>

            <div className="task-right-side">
                <span>
                    <FontAwesomeIcon onClick={active} className='check-icon icon ' icon={faCheck}  ></FontAwesomeIcon>
                    <FontAwesomeIcon onClick={active} className='thumbs-up-icon' icon={faThumbsUp}  ></FontAwesomeIcon>
                </span>
            </div>

            <div className="task-left-side">
                    <p> Task:     <span>  {   props.task     }  </span>  </p>
                    <FontAwesomeIcon 
                        onClick   =  {() => props.removing(props.id)} 
                        icon      =  { faTimes  } 
                        className =  'closeIcon'>
                    </FontAwesomeIcon>
            </div>

        </div>
    )
}

export default TodoItems
