import React, { Component } from 'react';
import AddItems from "./component/AddItems";
import TodoItems from "./component/TodoItems";

class App extends Component{

    constructor(){
        super();
        this.state = {
            info:[
                { name : "John",    },
                { name : "Adam" ,   },
                { name : "Micheal", },
            ]
        }
        this.removing = this.removing.bind(this)
        this.adding   = this.adding.bind(this)
    }

    mapping(arr){

        const checking = arr.length ? 
        arr.map(
            (res, id) => <TodoItems
            key      = {  id            }
            id       = {  id            }
            name     = {  res.name      }
            removing = {  this.removing }
            />
        )
        : <h1>asdsad</h1>
        return checking
    }

    adding(item){
        item.id = Math.random()
        const {info} = this.state
        info.push(item)
        this.setState({
            info
        })

    }
    removing(id){
        const {info} = this.state
        info.splice(id,1)
        this.setState({
            info
        })
    }


    render(){
        return(
            <div>
                <AddItems adding = {  this.adding  }/>
                <ul>{this.mapping(this.state.info)}</ul>
            </div>
        )
    }
}

export default App
