import React, { Component } from 'react'
import Box from './Box'
import member from './info'

class App extends Component{


constructor(){
    super()
    this.state ={
        member : member                                                                  
    }
}                        

showInfo(matrix){

        const m = matrix.map(
// The Arrow Function Should Not Be Wrraped with Curly Brasis
        person => 
            <Box
                        name = {  person.name }
                        img =  {  person.img  }
                        age =  {  person.age  }
                        job =  {  person.job  }
            />
        )

    return m;
}

render(){




    return(
    <div className='mio'>
        {this.showInfo(this.state.member)}
    </div>
    )
}


}


export default App
