import React, { Component } from 'react'
// import AddItems from './component/AddItems/AddItems'
import TodoItems from './component/TodoItems/TodoItems'


class App extends Component{

constructor(){
    super();
    this.state = {
        data:[
            {  id:1,   name: "John",      job: "Frontend",       country: "USA"      },
            {  id:2,   name: "Adam",      job: "Phliospher",     country: "Germany"  },
            {  id:3,   name: "Noah",      job: "Worker",         country: "Germany"  },
            {  id:4,   name: "Hiroki",    job: "Freelancer",     country: "Japan"    },
        ]
    }
    this.removing = this.removing.bind(this)
}




    mapping(arr){

        const checking = arr.length ? 
        arr.map(
            res=> <TodoItems 

            id        =    {  res.id          }
            name      =    {  res.name        }
            job       =    {  res.job         }   
            country   =    {  res.country     }
            removing  =    {  this.removing   }
            />
        )
        :
        <h1 className='warnning'>"No Info To Show !"</h1>;

        return checking

    }
    removing(id)  {

        const {data} = this.state;
        console.log(data.length);
        let index = data.findIndex( item=> item.id === id)
        data.splice(index,1)
        this.setState({
            data
        })

    }

    render(){
        return(
            <div>
                {this.mapping(this.state.data)}
            </div>
        )
    }
}

export default App
