import React, { Component } from 'react'
import Box from './Box';

class App extends Component{

  constructor(){
    super();
    this.state= {

    }
  }

  mapping(matrix){

    const myInfo = matrix.map(   
      person => 

        <Box
        name     = {  person.name    }
        img      = {  person.img     }
        job      = {  person.job     }
        number   = {  person.number  }
        email    = {  person.email   }

        />
    ) 

    return myInfo
  }

  render(){


    return(
      <div className='mio'>
        {this.mapping(this.state.info)}
      </div>
    )
  }
}


export default App
