import { faAdjust, faAlignRight, faCheck, faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { Component, Fragment } from 'react';

class TOdoItems extends Component{


    constructor(){
        super();
        this.state = {
            switcher : false
        }
    }
    isOn = ()=> {
        const {switcher} = this.state
        this.setState({
            switcher : !switcher,
        })
        console.log(switcher);
    }

    originalForm = ()=> {
        <li key={this.props.id}>
            <FontAwesomeIcon
                icon   = {this.state.switcher ? faCheck: faAlignRight}
                onClick = {()=> this.isOn()}
            >
            </FontAwesomeIcon>
            <span style={{textDecoration : this.state.switcher? "line-through" : "none"}}>Name: {this.props.name}</span>

            <FontAwesomeIcon 
                onClick   =  {() => this.props.removing(this.props.id)} 
                icon      =  { faTimes  } 
                className =  'closeIcon'
            >
            </FontAwesomeIcon>
            <span  style={{padding : "20px"}, {margin : "20px"} ,{background: "red"}}>edit</span>
        </li>
    }

    // handleEdit(){
    //     <input type="text" placeholder='Enter something' defaultValue={} />
    // }
    render(){
        return(
            <Fragment>
                <li>
                {this.originalForm()}
            </li>
            </Fragment>
        )
    }

}


export default TOdoItems
