import React from 'react'
import { Component } from 'react';
import "./SASS/style.css";

class AddItems extends Component{

    constructor(){
        super();
        this.state ={
            name:  "",
            job :  "",
            age :  "",
        }
        this.handleAdding = this.handleAdding.bind(this)
    }


    handleAdding(e){
        this.setState({
            [e.target.id] : e.target.value
        })
    handleSubmit = (e)=> {
        e.preventDefault();
        this.props.adding(this.state)
        console.log(this.state);

    }

    }

    render(){
        return(
            <form onSubmit={this.handleSubmit}>
                <input type="text" id='name'   placeholder='Enter name...'   onChange={this.handleAdding} />
                <input type="text" id='job '   placeholder='Enter job... '   onChange={this.handleAdding} />
                <input type="text" id='age '   placeholder='Enter age... '   onChange={this.handleAdding} />
                <input type="submit" value={"Submit"} />
            </form>
        )
    }

}

export default AddItems
