    import React from 'react'
    import About from './About'
    import Shop from './Shop'
    import Nav from './Nav'
    import Users from './Users'
    import { BrowserRouter , Routes, Route } from 'react-router-dom'
    import Rout from "./Rout";

    function App (){

            return(
                <BrowserRouter>

                <Nav/>
                {/* We Detect/Determine/Choose The Below Path by Nav. Meaning that Whatever path Is In The Nav Conponent, Will be Taken And Utilized below */}
                        <Routes>
                            <Route exclude path= '/Home.js' element={<Main/>} />
                            <Route path="/Shop.js"  element = {<Shop/>} />
                            <Route path="/Users.js" element = {<Users/>} />
                            <Route path="/About.js" element = {<About/>} />
                            <Route path= {'/Shop.js'} element = {<Rout/>} />
                            {/* 1- I need to use A particular path and this path will be taken as a parameter */}
                            {/* 2- Use the parameter and send it TO a component */}
                            {/* 3- Inside that Component, I want to utilize it and display it or do whatever I want. */}


                        </Routes>

                </BrowserRouter>



        );
    }

    function Main(){
        return(
        <div>
            <h1>Main Page</h1>
            <p>Welcome To The Main Page</p>
        </div>
        )
    }

export default App
