import React, {Component} from 'react';

class AddItems extends Component{

    constructor(){
        super();
        this.state = {
            name : ""
        }
        this.handleClick  = this.handleClick.bind (this) 
        this.handleChange = this.handleChange.bind(this) 
    }

    handleClick(){
        const myinput = document.querySelector(".myInput")
        if ( myinput.value.trim().length === 0 ) {
            alert("No")
        }else{
            console.log(this.state.name);
            this.props.adding(this.state)
            myinput.value = ""

        }

    }

    handleChange(e){
        this.setState({
            [e.target.id] : e.target.value
        })    
    }

    render(){
        return(
            <div className='myAddItemSection'>
                <input type="text" onChange={this.handleChange} placeholder="Enter something ..." id='name' className="myInput" /> 
                <button onSubmit={this.handleClick}>Send</button>
            </div>
        )
    }

}


export default AddItems
