    import React, { Component } from 'react'
    import Box from './Box'
    import member from './info'

    class App extends Component{

        constructor(){
            super()
            this.state= {
                theMembers : member,
                newArr : [],
                counter : 0,
            }
            this.clickme = this.clickme.bind(this)
        }


        clickme(){
            this.state.newArr.push(this.state.theMembers[this.state.counter])

            this.setState({
                function(prev){
                return {counter : prev.counter + 1}
            }})
            this.myMember = this.state.newArr.map( 

                person => 

                    <Box
                    name =  { person.name }
                    age  =  { person.age  }
                    img  =  { person.img  }
                    job  =  { person.job  }

                />
            )
        }



    render(){




        return(
<div className="">
    <button className='myBtn' onClick={this.clickme}>Click me</button>
        <div className='mio'>

            {this.myMembers}

        </div>
</div>
        )
    }


    }


    export default App
