import React from 'react'
import "./SASS/style.css";
import { faArrowUp, faThumbsUp, faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon }     from '@fortawesome/react-fontawesome';



let myIcons = Array.from (document.querySelectorAll('.box .icon'))
    myIcons.forEach( e => {
            // console.log(e)
        e.addEventListener('click', () => {
            console.log(e.value)
        })
});

            // myIcon.classList.toggle("active"); 

            // document.querySelector('.task-left-side p').classList.toggle("active")


            const TodoItems = (props)=>{


    return(
        <div  className='box'>
            <div className="task-right-side">
                <span>
                    <FontAwesomeIcon 
                    id         =  'thumbs' 
                    // onClick    =  {active} 
                    className  =  'thumbs-up-icon icon' 
                    icon       =  {faThumbsUp} >

                    </FontAwesomeIcon>
                </span>
            </div>

            <div className="task-left-side">
                    <p> Task:     <span>  {   props.task     }  </span>  </p>
                    <FontAwesomeIcon 
                        onClick   =  {() => props.removing(props.id)} 
                        icon      =  { faTimes  } 
                        className =  'closeIcon'>
                    </FontAwesomeIcon>
            </div>

        </div>
    )
}

export default TodoItems
