import React, { Component } from 'react'
class App extends Component{

  constructor(){
    super();
    this.state = {
      number : 0
    }

    this.chang = this.chang.bind(this);
  }

  change(){
    this.setState({
      number : 1

    })
  }

  render(){


    return(
      <div className='mio'>
          <div>Number: {this.state.number}</div>
        <button onClick={this.chang}>Click me</button>
      </div>
    )
  }
}


export default App
