import React from 'react'
import { Component } from 'react';
import "./SASS/style.css";

class AddItems extends Component{


    constructor(){
        super();
        this.state ={
            task: "",
            job : "",
            country : "",
        }
        this.handleAdding = this.handleAdding.bind(this)
    }


    handleAdding(e){
        this.setState({
            [e.target.id] : e.target.value
        })
    }

    handleSubmit = (e)=>{
        e.preventDefault();

        this.props.adding(this.state)
        let m = document.querySelectorAll('input')
        m.forEach( input => {
            input.value= ""

        });

    }

    render(){
        return(
            <form className='myForm' onSubmit={this.handleSubmit}>
                <div>
                    <label className='task-Label'>Task: </label> <br />
                    <input type="text" id='name'      placeholder='Enter Task...'       onChange={this.handleAdding} />
                </div>
                <input id='submitInput' type="submit" value={"Submit"}  />
            </form>
        )
    }




}

export default AddItems
