import React, { Component } from 'react'
import Box from './Box'

class App extends Component{


constructor(){
    super()
    this.state ={
    member : [
            {  name: "Ali" ,   img: 'https://picsum.photos/250',   age: 24,    job: "Frontend"  },
            {  name: "Adam",   img: 'https://picsum.photos/251',   age: 66,    job: "Backend"   },
            {  name: "Kaz" ,   img: 'https://picsum.photos/252',   age: 32,    job: "AI"        },
    ]                                                                                     
    }
}                        

showInfo(matrix){

        const m = matrix.map(
// The Arrow Function Should Not Be Wrraped with Curly Brasis
        person => 
            <Box
                        name = {  person.name }
                        img =  {  person.img  }
                        age =  {  person.age  }
                        job =  {  person.job  }
            />
        )

    return m;
}

render(){




    return(
    <div className='mio'>
        {this.showInfo(this.state.member)}
    </div>
    )
}


}


export default App
