    import React, { Component } from 'react'
    // import Box from './Box'
    // import member from './info'

    class App extends Component{

        constructor(){
        }


        plus(){

            this.setState((prevState) => ({
                myNumber: prevState.myNumber + 1
            }))
        }
        minus(){

            this.setState((prevState) => ({
                myNumber: prevState.myNumber - 1
            }))
        }

        render(){
            console.log("Render");
            return(
            <div className="">
                <button onClick={this.plus}> + </button> <span>{this.state.myNumber}</span> <button onClick={this.minus} > - </button>
            </div>
        )
    }
}


    export default App
