import React, { Component } from 'react'
import Box from './Box'

class App extends Component{


  constructor(){
    super()
    this.state =[
      {  name: "Ali" ,   img: 'https://picsum.photos/250',   age: 24,    job: "Frontend"  },
      {  name: "Adam",   img: 'https://picsum.photos/251',   age: 66,    job: "Backend"   },


    ]
  }
  render(){

    const info = [
      {  name: "Kaz" ,   img: 'https://picsum.photos/252',   age: 32,    job: "AI"        },
    ]

    // Extract Every Single One

    const showInfo = info.map ( person => {
      return <Box
      name = {  person.name }
      img =  {  person.img  }
      age =  {  person.age  }
      job =  {  person.job  }

      />
    })

    return(
      <div className='mio'>
        {showInfo}
      </div>
    )
  }


}


export default App
