import React, { Component } from 'react';
import AddItems from "./component/AddItems";
import TodoItems from "./component/TodoItems";

class App extends Component{

    constructor(){
        super();
        this.state = {
            info :[
                {task: "John" },
                {task: "Adam" },
                {task: "Noah" },
            ]
        }
        this.map = this.map.bind(this)
        this.adding = this.adding.bind(this)
    }

    map(matrix){
        return(
            matrix.map(
            (res, id) => <TodoItems
            key  = {  id        }
            id   = {  id        }
            task = {  res.task  }
            />
        ))
    }

    removing(id){
        const {info} = this.state;
        let myIndex = info.findIndex();

    }
    adding(item){
        item.id = Math.random()
        const {info} = this.state;
        info.push(item)
        this.setState({
            info
        })
    }

    render(){
        return(
            <div>
                <AddItems
                adding = {this.adding}
                />
                <ul>
                    {this.map(this.state.info)}
                </ul>
            </div>
        )
    }
}

export default App
