                    <FontAwesomeIcon 
                        onClick   =  {() => this.props.removing(this.props.id)} 
                        icon      =  { faTimes  } 
                        className =  'closeIcon'
                    >
                    </FontAwesomeIcon>
                </li>
            </Fragment>
        )
    }

}


export default TOdoItems
