import { Component } from 'react';
import "./SASS/style.css";

class AddItems extends Component{


    constructor(){
        super();
        this.state ={
            name: "",
            job : "",
            age : "",
        }
        this.addItem = this.addItem.bind(this)
    }



    addItem(e){

        this.setState({
            [e.target.id] : e.target.value
        })
    }

    handleSubmit = (e)=>{
        e.preventDefault();
        console.log(this.state);
        this.props.adding(this.state)

    }

    render(){
        return(
            <form onSubmit={this.handleSubmit}>
                <input type="text" id='name'   placeholder='Enter name...'   onChange={this.addItem} />
                <input type="text" id='job '   placeholder='Enter job... '   onChange={this.addItem} />
                <input type="text" id='age'   placeholder='Enter age... '   onChange={this.addItem} />
                <input type="submit" value={"Submit"}  />
            </form>
        )
    }
