import React, { Fragment } from 'react'
import "./SASS/style.css";
import { faThumbsUp, faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon }     from '@fortawesome/react-fontawesome';


const TodoItems = (props)=>{

    function active (e){

        // e.style.display="none"            

    }

    return(
        <Fragment >

        <div key={props.id} className='box'>

{
    active()

}
            <div className="task-right-side">
                <span>
                    <FontAwesomeIcon 
                    // id         =  'thumbs' 
                    onClick    =  {active} 
                    className  =  'thumbs-up-icon icon' 
                    icon       =  {faThumbsUp} >

                    </FontAwesomeIcon>
                </span>
            </div>

            <div className="task-left-side">
                    <p> Task:     <span>  {   props.task     }  </span>  </p>
                    <FontAwesomeIcon 
                        onClick   =  {() => props.removing(props.id)} 
                        icon      =  { faTimes  } 
                        className =  'closeIcon'>
                    </FontAwesomeIcon>
            </div>

        </div>
        <div key={props.id} className='box'>
            <div className="task-right-side">
                <span>
                    <FontAwesomeIcon 
                    // id         =  'thumbs' 
                    onClick    =  {active} 
                    className  =  'thumbs-up-icon icon' 
                    icon       =  {faThumbsUp} >

                    </FontAwesomeIcon>
                </span>
            </div>

            <div className="task-left-side">
                    <p> Task:     <span>  {   props.task     }  </span>  </p>
                    <FontAwesomeIcon 
                        onClick   =  {() => props.removing(props.id)} 
                        icon      =  { faTimes  } 
                        className =  'closeIcon'>
                    </FontAwesomeIcon>
            </div>

        </div>
        <div key={props.id} className='box'>
            <div className="task-right-side">
                <span>
                    <FontAwesomeIcon 
                    // id         =  'thumbs' 
                    onClick    =  {active} 
                    className  =  'thumbs-up-icon icon' 
                    icon       =  {faThumbsUp} >

                    </FontAwesomeIcon>
                </span>
            </div>

            <div className="task-left-side">
                    <p> Task:     <span>  {   props.task     }  </span>  </p>
                    <FontAwesomeIcon 
                        onClick   =  {() => props.removing(props.id)} 
                        icon      =  { faTimes  } 
                        className =  'closeIcon'>
                    </FontAwesomeIcon>
            </div>

        </div>
        </Fragment>
    )
}

export default TodoItems
