    import React, { Component } from 'react'
    import Box from './Box'
    import member from './info'

    class App extends Component{

    constructor(){
        super()
        this.state ={
            member : member,       
            newMember : [],

        }
    this.click = this.click.bind(this)
    }


    click(){

        this.state.newMember.push(this.state.member[1]);
        console.log(this.state.newMember)

        this.myMember = this.state.newMember.map(

            person => 
                <Box
                            name = {  person.name }
                            img =  {  person.img  }
                            age =  {  person.age  }
                            job =  {  person.job  }
                />
            )

    }

    render(){




        return(
<div className="">
    <button className='myBtn' onClick={this.click}>Click me</button>
        <div className='mio'>
            {this.state.newMember}
        </div>
</div>
        )
    }


    }


    export default App
