import React, { Component } from "react";
import Employee from './Empolyees'

class App extends Component{


  render(){
        // List of Object
    const matrix= [

      {id: 1,     img:'https://picsum.photos/250',    name:'Leo',     job : 'Actor',        phone : '+964782232112',     email : 'asd@leonardo.com',     website : 'www.leo.com',},
      {id: 2,     img:'https://picsum.photos/250',    name:'John',    job : 'Freelancer',   phone : '+964782232112',     email : 'bfd@leonardo.com',     website : 'www.leo.com',},
      {id: 3,     img:'https://picsum.photos/250',    name:'Man',     job : 'Eng',          phone : '+964782232112',     email : '2dd@leonardo.com',     website : 'www.leo.com',},
      {id: 4,     img:'https://picsum.photos/250',    name:'Womm',    job : 'Programmer',   phone : '+964782232112',     email : 'leo@leonardo.com',     website : 'www.leo.com',},

    ]
    // calling the matrix information and save them within a variable.
    // We can't access the matrix information without a map function.
    // save the result in a variable
    // Call the variable in the below JSX tag
    const mem= matrix.map((empolyee) => {
      return(
                    <Employee 
                    id      = {empolyee.id       }   
                    name    = {empolyee.name     }
                    img     = {empolyee.img      }
                    job     = {empolyee.job      }
                    phone   = {empolyee.phone    }
                    email   = {empolyee.email    }
                    website = {empolyee.website  }
                  />  
    )
  })

    return(

      <div>

        {mem}

      </div>
    )
  }

}

// 0- What is happening here is that I don't have to repeat myself anymore. 
// 1- I have the info in the matrix.
// 2- I store their values using map function. Saved map in a varable
// 3- I called the variable only once.
// 4- Every time I need to add info, i don't call the exported component, but just add it once in the matrix.


export default App;
