import React from 'react'
import "./SASS/style.css";
import { faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


const TodoItems = (props)=>{
    const {items, removing} = props;
    const mimo = items.map(
        res =>{
            return(
                <div className='infoContainer' key={res.id}>
                    <FontAwesomeIcon 
                    onClick   =  { ()=> removing(res.id) } 
                    icon      =  { faTimes  } 
                    className =  'closeIcon'>
                    </FontAwesomeIcon>


                    <p>                   {   res.id       } </p>
                    <p> Name:     <span>  {   res.name     }  </span>  </p>
                    <p> Job:      <span>  {   res.job      }  </span>  </p>
                    <p> Country:  <span>  {   res.country  }  </span>  </p>
                </div>
            )
        }
    )
    return(
        <div>

                            <FontAwesomeIcon 
                    onClick   =  { ()=> removing(res.id) } 
                    icon      =  { faTimes  } 
                    className =  'closeIcon'>
                    </FontAwesomeIcon>


                    <p>                   {   res.id       } </p>
                    <p> Name:     <span>  {   res.name     }  </span>  </p>
                    <p> Job:      <span>  {   res.job      }  </span>  </p>
                    <p> Country:  <span>  {   res.country  }  </span>  </p>

        </div>
    )
}

export default TodoItems
