    this.state ={

      myMember : this.clickEvent(myArr)

    }
  }   



  clickEvent(newArr){

  }








  showInfo(matrix){

    for (let i = 0; i < member.length; i++) {

      matrix.push(member[i])

        const m = matrix.map(
          // The Arrow Function Should Not Be Wrraped with Curly Brasis
          person => 
            <Box
              name  =    {  person.name  }
              img   =    {  person.img   }
              age   =    {  person.age   }
              job   =    {  person.job   }
              phone =    {  person.phone }
              email =    {  person.email }
            />
          )

        }


    return matrix;
  }




  render(){





    return(
      <div>
        <button className='myBtn' onClick={this.clickEvent}>Click me</button>
        <div className='mio'>
          {this.showInfo(this.state.myMember)}
        </div>
      </div>
    )
  }


}


export default App
