            name: "",
            job : "",
            age : "",
        }
    }

    addItem(e){
        console.log(e.target.value);
    }
    render(){
        return(
            <div className=''>
                <input onChange={this.addItem}  id  type="text" placeholder='Enter A Name : ' />
                <input onChange={this.addItem}  id  type="text" placeholder='Enter A Job  : ' />
                <input onChange={this.addItem}  id  type="text" placeholder='Enter An Age : ' />
            </div>  
        )
    }
}

export default AddItems
