import React, { Component } from 'react'
import Box from './Box'
import member from './info';
class App extends Component{


  constructor(){
    super()
    this.state ={  myMember : member  }
  }   

  showInfo(matrix){
    let newArr = []
    for (let i = 0; i < member.length; i++) {

      newArr.push(

        matrix.map(
            // The Arrow Function Should Not Be Wrraped with Curly Brasis
            person => 
              <Box
                name  =    {  person.name  }
                img   =    {  person.img   }
                age   =    {  person.age   }
                job   =    {  person.job   }
                phone =    {  person.phone }
                email =    {  person.email }
              />
          )
      )
        }
        return newArr;
  }




  render(){

    return(
      <div>
        <button className='myBtn' onClick={this.showInfo}>Click me</button>
        <div className='mio'>
          {this.showInfo(this.state.myMember)}
        </div>
      </div>
    )
  }


}


export default App
