import React from 'react'
import { Component } from 'react';
import "./SASS/style.css";

class AddItems extends Component{


    constructor(){
        super();
        this.state ={
            name: "",
            job : "",
            country : "",
        }
        this.handleAdding = this.handleAdding.bind(this)
    }


    handleAdding(e){
        this.setState({
            [e.target.id] : e.target.value
        })
    }

    handleSubmit = (e)=>{
        e.preventDefault();
        this.props.adding(this.state)
        let m = document.querySelectorAll('input')
        m.forEach( input => {
            input.value= ""
        });
    }

    render(){
        return(
            <form className='myForm' onSubmit={this.handleSubmit}>
                <label htmlFor=""></label>
                <input type="text" id='name'      placeholder='Enter name...'       onChange={this.handleAdding} />
                <input type="text" id='job'       placeholder='Enter job... '       onChange={this.handleAdding} />
                <input type="text" id='country'   placeholder='Enter country... '   onChange={this.handleAdding} />
                <input id='submitInput' type="submit" value={"Submit"}  />
            </form>
        )
    }




}

export default AddItems
