import { faAdjust, faAlignRight, faCheck, faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { Component, Fragment } from 'react';

class TOdoItems extends Component{


    constructor(){
        super();
        this.state = {
            switcher : true
        }
    }
    isOn = ()=> {
        const {switcher} = this.state
        this.setState({
            switcher : !switcher,
        })
        console.log(switcher);
    }

    originalForm (){
        return(
            <Fragment>

                <FontAwesomeIcon
                    icon   = {this.state.switcher ? faCheck: faAlignRight}
                    onClick = {()=> this.isOn()}
                >
                </FontAwesomeIcon>
                <span style={{textDecoration : this.state.switcher? "line-through" : "none"}}>Name: {this.props.name}</span>

                <FontAwesomeIcon 
                    onClick   =  {() => this.props.removing(this.props.id)} 
                    icon      =  { faTimes  } 
                    className =  'closeIcon'
                >
                </FontAwesomeIcon>
                <span onClick={this.isOn()} style={{padding : "20px"} , {margin : "20px"} ,{background: "red"}}>edit</span>
            </Fragment>

        )
    }

    editForm(){
        return(
            <Fragment>
                <input type="text" placeholder='Enter Something ...' />
            </Fragment>
        )
    }
    render(){
        return(
                <li key={this.props.id}>
                    {this.state.switcher? this.originalForm() : this.editForm()}
                </li>
        )
    }

}


export default TOdoItems
