import React from 'react'
import "./SASS/style.css";
import { faCheck, faThumbsUp, faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


const TodoItems = (props)=>{

    const active = ()=>{
        document.querySelector('.icon').addEventListener('click', 
            (e) => {
                    e.classList.toggle= "active";
                    console.log(e.id);
            })
    }

    return(

        <div key={props.id} className='box'>

            <div className="task-right-side">
                <span>
                    <FontAwesomeIcon id='thumbs' onClick={active} className='thumbs-up-icon icon' icon={faThumbsUp} > </FontAwesomeIcon>
                </span>
            </div>

            <div className="task-left-side">
                    <p> Task:     <span>  {   props.task     }  </span>  </p>
                    <FontAwesomeIcon 
                        onClick   =  {() => props.removing(props.id)} 
                        icon      =  { faTimes  } 
                        className =  'closeIcon'>
                    </FontAwesomeIcon>
            </div>

        </div>
    )
}

export default TodoItems
