import React, { Component } from 'react'
import Box from './Box';

class App extends Component{

  constructor(){
    super();
    this.state= {
      info : [
        {name: "Ali"  ,  img: "",   job: "Frontend Dev.",      number: "+96467822278864",     email: "ali.akbar@myakbar.com"   },
        {name: "John" ,     job: "Philospher.  ",      number: "+92 444 123    ",     email: "John@myHonny.com"        },
        {name: "Noah" ,     job: "Backend Dev. ",      number: "+91 321 258    ",     email: ""                        },
        {name: "Yo"   ,     job: "Marketing.   ",      number: "+2 412 2221    ",     email: "Salter.yo@gmail.com"     },
        {name: "kaz"  ,     job: "AI."          ,      number: ""               ,     email: "kazmyoma@hotmail.com"    },
      ]   
    }
  }

  mapping(matrix){

    const myInfo = matrix.map(   
      person => 

        <Box
        name     = {  person.name    }
        job      = {  person.job     }
        number   = {  person.number  }
        email    = {  person.email   }

        />
    ) 

    return myInfo
  }

  render(){


    return(
      <div className='mio'>
        {this.mapping(this.state.info)}
      </div>
    )
  }
}


export default App
