import React, { Component } from 'react'
import Box from './Box'
import member from './info';
class App extends Component{


  constructor(){
    super()

    const newArr = [];

    for (let i = 0; i < matrix.length; i++) {

      newArr.push(member[i])
    }

    this.state ={

      member : member

    }
  }   


  newBox(matrix){

    const newArr = [];

    for (let i = 0; i < matrix.length; i++) {

      newArr.push(matrix[i])

    }
    return newArr

  }










  showInfo(matrix){

          const m = matrix.map(
// The Arrow Function Should Not Be Wrraped with Curly Brasis
          person => 
            <Box
                          name  =    {  person.name  }
                          img   =    {  person.img   }
                          age   =    {  person.age   }
                          job   =    {  person.job   }
                          phone =    {  person.phone }
                          email =    {  person.email }
            />
          )

    return m;
  }




  render(){





    return(
      <div>
        <button className='myBtn' onClick={this.newBox}>Click me</button>
        <div className='mio'>
        {this.showInfo(this.state.member)}
      </div>
      </div>
    )
  }


}


export default App
