import React, {Component} from 'react';



class AddItems extends Component{

    takeValue(e){
        console.log(e.target.value);
    }


    render(){
        return(
            <div className='myAddItemSection'>
                <form onSubmit={this.props.submitValue} action="">
                    <input onChange={this.takeValue} type="text" placeholder={"Enter the name: "} />
                    <button>Send</button>
                </form>
            </div>
        )
    }

}


export default AddItems
