function Box(){


    const [number, math] = useState(5)


    function decreament(){
        math ( (num) => num - 1)
    }
        return(
            <div className='box'>
                <button>+</button>
                <span>{number}</span>
                <button onClick={decreament}>-</button>
            </div>
        )

}


export default Box
