import React, { Component } from 'react'

class App extends Component{

  constructor(){
    super();
    this.state = {
      num: 0
    }
    this.changNum = this.changNum.bind(this)
  }

  changNum(){
    this.setState({
      num:1
    })
  }

  render(){
    return(
      <div className='mio'>
        <div>{this.state.num}</div>
        <button onClick={this.changNum}>Click Me</button>
      </div>
    )
  }


}


export default App
