    import React, { Component } from 'react'
    import Box from './Box'
    import member from './info'

    class App extends Component{

    constructor(){
        super()
        this.state ={
            member : member,       
            newMember : [],
            counter : 0
        }
    this.click = this.click.bind(this)
    }


    click(){

        if (this.state.counter < this.state.member) {
            this.state.newMember.push(this.state.member[this.state.counter]);
            this.setState({
            function(prev) {
                return {counter: prev + 1}
            }
        });

        this.myMember = this.state.newMember.map(

            person => 
                <Box
                            name = {  person.name }
                            img =  {  person.img  }
                            age =  {  person.age  }
                            job =  {  person.job  }
                />
            )
        } else {
            return false
        }

    }

    render(){




        return(
<div className="">
    <button className='myBtn' onClick={this.click}>Click me</button>
        <div className='mio'>
            {this.myMember}
        </div>
</div>
        )
    }


    }


    export default App
