import axios from 'axios';
import React, { Component } from 'react';
import CityInput  from "./component/CityInput";
import ResultWeather from "./component/ResultWeather";
class App extends Component{

    constructor(){
        super();
        this.state = {
            location : "",
            temp : "",
            humidity : "",
            describtion : "",
        }
        this.getWeather = this.getWeather.bind(this)
    }

    getWeather(e){
        e.preventDefault()
        const city = e.target.elements.city.value

        if (city.trim().length !==0 ) {
            axios(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=e36ed364400282e43250b6c4c0274d44`)
            .then(
                res => 
                this.setState({
                    location :     res.data.name,
                    temp :         res.data.main.temp,
                    humidity :     res.data.main.humidity,
                    describtion :  res.data.weather[0].description,
                })


            )
        }else{
            alert("Nope")
        }

        console.log(this.state.location)
        console.log(this.state.describtion);
        console.log(this.state.humidity);


    }

    render(){

        return(
            <div className='container'>
                <CityInput getWeather = { this.getWeather } />

                <ResultWeather/>
            </div>
        )
    }
}

export default App
