import React, {Component} from 'react';



class AddItems extends Component{

    constructor(){
        super();
        this.state = {
            task : "",
        }
        this.submitValue = this.submitValue.bind(this)
        this.takeValue   = this.takeValue.bind(this)
    }

    takeValue(e){
        this.setState({
            task : e.target.value
        })
    }

    submitValue(e){
        e.preventDefault()
        this.props.adding(this.state.task)
    }

    render(){
        return(
            <div className='myAddItemSection'>
                <form onSubmit={this.submitValue} action="">
                    <input onChange={this.takeValue()} type="text" placeholder={"Enter the name: "} />
                    <button>Send</button>
                </form>
            </div>
        )
    }

}


export default AddItems
