import React, { Component } from 'react'
import Box from './Box'
import member from './info';
class App extends Component{


  constructor(){
    super()

    let newArr=[]
    this.state ={  myMember : member  }
  }   

  showInfo(matrix){
    for (let i = 0; i < member.length; i++) {
      this.newArr.push(
        matrix.map(
            // The Arrow Function Should Not Be Wrraped with Curly Brasis
            person => 
              <Box
                name  =    {  person.name  }
                img   =    {  person.img   }
                age   =    {  person.age   }
                job   =    {  person.job   }
                phone =    {  person.phone }
                email =    {  person.email }
              />
          )
      )
    }
    return this.newArr;
  }


  render(){
    return(
      <div>
        <button className='myBtn' 
        onClick={this.showInfo(this.state.myMember)}>
          Click me
        </button>
        <div className='mio'>
          {this.newArr}
        </div>
      </div>
    )
  }


}


export default App
