import React from 'react'
import "./SASS/style.css";
import { faArrowUp, faThumbsUp, faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon }     from '@fortawesome/react-fontawesome';


const TodoItems = (props)=>{

    const active = ()=>{

        let myBox = document.querySelectorAll('.box') 
        for (let i = 0; i < myBox.length; i++) {
            let myIcon = document.querySelector('.icon')
            console.log(myIcon.id);
            myBox[i].classList.toggle("active"); 

            // document.querySelector('.task-left-side p').classList.toggle("active")
        }
    }

    return(
        <div  className='box'>
            <div className="task-right-side">
                <span>
                    <FontAwesomeIcon 
                    id         =  'thumbs' 
                    onClick    =  {active} 
                    className  =  'thumbs-up-icon icon' 
                    icon       =  {faThumbsUp} >

                    </FontAwesomeIcon>
                </span>
            </div>

            <div className="task-left-side">
                    <p> Task:     <span>  {   props.task     }  </span>  </p>
                    <FontAwesomeIcon 
                        onClick   =  {() => props.removing(props.id)} 
                        icon      =  { faTimes  } 
                        className =  'closeIcon'>
                    </FontAwesomeIcon>
            </div>

        </div>
    )
}

export default TodoItems
