import React from 'react'
import { Component } from 'react';
import "./SASS/style.css";

class AddItems extends Component{


    constructor(){
        super();
        this.state ={
            task: "",
        }
        this.handleAdding = this.handleAdding.bind(this)
    }


    handleAdding(e){
        this.setState({
            [e.target.id] : e.target.value
        })
    }

    handleSubmit = (e)=>{
        e.preventDefault();
        let myInputs = document.querySelector('#submitInput')
        myInputs.forEach( input => {
            console.log(input.value.trim().length);
            if (input.value.trim().length !== 0 ) {
                this.props.adding(this.state)
                input.value= ""

            }

        });
    }

    render(){
        return(
            <form className='myForm' >
                <div>
                    <label className='task-Label'>Task: </label> <br />
                    <input type="text" id='task'      placeholder='Enter task...'       onChange={this.handleAdding} />
                </div>
                <input id='submitInput' type="submit" value={"Submit"} onSubmit={this.handleSubmit} />
            </form>
        )
    }




}

export default AddItems
