import React, { Component } from 'react';
import AddItems from "./component/AddItems";
import TodoItems from "./component/TodoItems";

class App extends Component{

    constructor(){
        super();
        this.state = {
            info :[
                {name: "John" , job: "Frontend"},
                {name: "Adam" , job: "Backend"},
                {name: "Noah" , job: "AI"},
            ]
        }
        this.map = this.map.bind(this)
    }

    map(matrix){
        return(matrix.map(
            (res, id) => <TodoItems
            key  = {id        }
            name = {res.name  }
            job  = {res.job   }
            />
        ))
    }

    render(){
        return(
            <div>
                {/* <TodoItems/>
                <AddItems/> */}
                <ul>
                    {this.map(this.state.info)}
                </ul>
            </div>
        )
    }
}

export default App
